#ifndef BVL_Y__INPUT
#define BVL_Y__INPUT(buf,result,max_size) \
	if ( bvl_y__current_buffer->bvl_y__is_interactive ) \
		{ \
		int c = '*', n; \
		for ( n = 0; n < max_size && \
			     (c = getc( bvl_y_in )) != EOF && c != '\n'; ++n ) \
			buf[n] = (char) c; \
		if ( c == '\n' ) \
			buf[n++] = (char) c; \
		if ( c == EOF && ferror( bvl_y_in ) ) \
			BVL_Y__FATAL_ERROR( "input in flex scanner failed" ); \
		result = n; \
		} \
	else if ( ((result = fread( buf, 1, max_size, bvl_y_in )) == 0) \
		  && ferror( bvl_y_in ) ) \
		BVL_Y__FATAL_ERROR( "input in flex scanner failed" );
#endif

